mapper.select:
select  from menu_summary
where date_sub(curdate(), INTERVAL #{recentDays} DAY) <= create_time order by score desc limit #{start},
#{limit}

select  from menu_summary
where date_sub(curdate(), INTERVAL #{recentDays} DAY) <= create_time order by create_time desc limit
#{start}, #{limit}

select  from menu_summary
where YEAR(create_time) = YEAR(now())
order by score desc limit #{start}, #{limit}

select  from menu_summary
where uuid in (select menu_id from category_menu_rel r where r.category_id = #{category})
order by recommend_num desc limit #{start}, #{limit}
